pragma solidity ^0.5.0;

contract Decentragram {
  string public name;
  uint public imageCount = 0;
  mapping(uint => Image) public images;

  mapping(address => mapping(uint => Image)) public directImages;
  mapping(address => uint) public directCount;

  struct Image {
    uint id;
    string hash;
    string description;
    uint tipAmount;
    address payable author;
  }

  event ImageCreated(
    uint id,
    string hash,
    string description,
    uint tipAmount,
    address payable author
  );
  event MessageSent(
    uint id,
    string hash,
    string description,
    uint tipAmount,
    address payable author,
    address receiver

  );

  event ImageTipped(
    uint id,
    string hash,
    string description,
    uint tipAmount,
    address payable author
  );

  constructor() public {
    name = "Decentragram";
  }

  function uploadImage(string memory _imgHash, string memory _description) public {
    // Make sure the image hash exists
    require(bytes(_imgHash).length > 0);
    // Make sure image description exists
    require(bytes(_description).length > 0);
    // Make sure uploader address exists
    require(msg.sender!=address(0));

    // Increment image id
    imageCount ++;

    // Add Image to the contract
    images[imageCount] = Image(imageCount, _imgHash, _description, 0, msg.sender);
    // Trigger an event
    emit ImageCreated(imageCount, _imgHash, _description, 0, msg.sender);
  }

  function directMessage(address _receiver, string memory _imgHash, string memory _description) public{
    require(msg.sender!=address(0));
    require(_receiver!=address(0));
    require(_receiver==address(_receiver));

    uint _count = directCount[_receiver];
    directCount[_receiver] = _count+1;

    directImages[_receiver][directCount[_receiver]] = Image(_count+1, _imgHash, _description, 0, msg.sender);

    emit MessageSent(_count+1, _imgHash, _description, 0, msg.sender,_receiver);

  }

  function tipImageOwner(uint _id) public payable {
    // Make sure the id is valid
    require(_id > 0 && _id <= imageCount);
    // Fetch the image
    Image memory _image = images[_id];
    // Fetch the author
    address payable _author = _image.author;

    require(_author != msg.sender);
    // Pay the author by sending them Ether
    address(_author).transfer(msg.value);
    // Increment the tip amount
    _image.tipAmount = _image.tipAmount + msg.value;
    // Update the image
    images[_id] = _image;
    // Trigger an event
    emit ImageTipped(_id, _image.hash, _image.description, _image.tipAmount, _author);
  }
}
